var Binder=function(n,t){this.location=function(){$(window).on("hashchange",function(){var i=window.location.hash.substring(1);n.standsby?n.hop(i):t.hasten().then(function(){t.unhasten(),n.hop(i)})})},this.input=function(){$(document).keypress(function(t){if(n.standsby)switch(t.which){case 13:n.standby(),n.hop($(".input").text());break;case 8:$(".input").text($(".input").text().slice(0,-1));break;default:$(".input").append(String.fromCharCode(t.which))}})}};